        backgroundcolor: "#F1C40F",
        cat_body: "B√© y√™u ·ªõi ∆°i",
      },
      {
        cat_id: "4",
        cat_name: "üîä G·ªçi cho anh ƒë√™ üîä",
        backgroundcolor: "#2ECC71",
        cat_body: "G·ªçi cho tuiiii li·ªÅnnn",
      },
    ],
    change: false,
  });

  const chooseItem = (token, item) => {
    // const token = "ExponentPushToken[HsWRXWGhvRSq6VAwM_RTG0]";
    switch (item.cat_id) {
      case "1":
        sendPushNoti(token, item.cat_name, item.cat_body);
        break;
      case "2":
        sendPushNoti(token, item.cat_name, item.cat_body);
        break;
      case "3":
        sendPushNoti(token, item.cat_name, item.cat_body);
        break;
      case "4":
        sendPushNoti(token, item.cat_name, item.cat_body);
